const Mockdata  = require("./mock/index.js");

module.exports=(app)=>{

    app.get("/api/list",(req,res)=>{
        const {values} = req.query;
        Mockdata.data.find((item,index)=>{
            if(item.pass ===  JSON.parse(values).password){
                res.send({
                    code:200
                })
            }else{
                res.send({
                    code:500
                })
            }
        })
    })


    app.post("/api/lists",(req,res)=>{
        res.send({
            code:200,
            data:Mockdata.lists
        })
    })



    app.post("/api/sort",(req,res)=>{
        res.send({
            code:200,
            data:Mockdata.lists.sort((x,y)=>{
                return x.num  - y.num
            })
        })
    })
}